mapper.select:
SELECT *
FROM o_client
where is_used = 'Y'



            and member_id = #{keyword}


            and name = #{keyword}


            and telephone = #{keyword}

mapper.update:
UPDATE o_client_manager
SET

    manager_name = #{managerName}


    manager_department = #{managerDepartment}


    manager_position = #{managerPosition}


    manager_phone_number = #{managerPhoneNumber}


    manager_email = #{managerEmail}

WHERE client_uniq_id = #{clientUniqId} and is_used = 'Y'
