mapper.update:
UPDATE o_estimate SET
client_name = #{clientName},
estimate_main = #{estimateMain},
estimate_main_etc = #{estimateMainEtc},
estimate_name = #{estimateName}
WHERE estimate_uniq_no = #{estimateUniqNo}

mapper.delete:
UPDATE o_estimate
SET is_used = 'N'
WHERE estimate_uniq_no = #{estimateUniqNo}
